-- inner join open_sellers sel
-- 		on u.user_id = sel.seller_id
Where user_id = 20228934

GROUP by 1,2









SELECT lstg.city
	,lstg.ll
	,ck.gmv_GBP
	,ck.sold_items
	,ck.sellers
	,ck.buyers
	,trfc.vi
	,trfc.imp
FROM
	(
	Select sel.city
		,count(distinct lstg.item_id) as ll
	From DW_LSTG_ITEM lstg
	inner join open_sellers sel
		on lstg.slr_id = sel.seller_id
	Where 1=1
		and lstg.LSTG_STATUS_ID = 0	--Only live listings
		and lstg.ITEM_SITE_ID = 3
	Group by 1
	) lstg
LEFT JOIN
	(
	Select sel.city
		,sum(ck.gmv_lc_amt) as gmv_GBP
		,sum(ck.QUANTITY) as sold_items
		,count(distinct sel.seller_id) as sellers
		,count(distinct ck.BUYER_ID) as buyers
	From DW_CHECKOUT_TRANS ck
	inner join open_sellers sel
		on ck.SELLER_ID = sel.seller_id
	inner join DW_CAL_DT cal
		on ck.gmv_dt = cal.CAL_DT
		and cal.AGE_FOR_DT_ID >= -365
	Where 1=1
		and ck.SITE_ID = 3
	Group by 1
	) ck 
		on lstg.city = ck.city
LEFT JOIN
	(
	Select sel.city
		,sum(trfc.TTL_VI_CNT) as VI
		,sum(trfc.SRP_IMPRSN_CNT)+sum(trfc.STORE_IMPRSN_CNT) as IMP
	From PRS_RESTRICTED_V.SLNG_TRFC_SUPER_FACT trfc
	inner join open_sellers sel
		on trfc.SELLER_ID = sel.seller_id
	inner join DW_CAL_DT cal
		on trfc.CAL_DT = cal.CAL_DT
		and cal.AGE_FOR_DT_ID >= -365	
	Where 1=1
		and trfc.SITE_ID = 3
	Group by 1
	) trfc 
		on trfc.city = lstg.city

